var app = angular.module('AltimitForum', ['ngRoute']);

angular.module('AltimitForum')
.config(function($routeProvider){
  $routeProvider
  .when('/', {
    templateUrl: 'views/main.html',
    controller: 'msgCtrl',
    controllerAs: 'Ctrl'
  })
  .otherwise({
    templateUrl: '/'
  });
});

angular.module('AltimitForum')
.controller('msgCtrl', function($scope){
  var me = this;

  me.msg = 'Test';
});
